var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate) {
		var now = new GlideDateTime();
		var dateToTest = new GlideDateTime(strDate);
		return dateToTest.before(now);
	},
	isDateToday: function(strDate) {
		var now = (new GlideDateTime()).getLocalDate();
		var dateToTest = (new GlideDateTime(strDate)).getLocalDate();
		var test = now.compareTo(dateToTest) === 0;
		return now.compareTo(dateToTest) === 0;
	},
    type: 'NeedItUtils'
};
